mapper.select:
SELECT department_id , department_name , manager_id , location_id FROM departments
OR department_id = #{departmentId}
OR job_id = #{jobId}
ORDER BY employee_id

mapper.insert:

	SELECT NVL(MAX(department_id), 0) +1
	FROM departments

INSERT INTO departments
		(
		department_id
	   	, department_name
	   	, manager_id

	   	, location_id		        	

        )
VALUES  (
		#{departmentId}
        , #{departmentName}
        , #{managerId}


        , #{locationId}

        )


SELECT departments_seq.CURRVAL
FROM dual				  

INSERT INTO departments
		(
		department_id
        , department_name
        , manager_id

        , location_id

        )
VALUES  (
		departments_seq.NEXTVAL
        , #{department_id}
        , #{department_name}
        , #{manager_id}


        , #{location_id}

        )

mapper.update:
